mapper.select:
SELECT ALRT.ALERT_ID, ALRT.ALERT_KEY as alertType, ALRT.ALERT_NAME, ALRT.TEMPLATE_ID, ST.TEMPLATE_KEY, ALRT.DISPLAY_SEQUENCE, ALRT.HEADER_ID, ALRT.VISIBILITY_VENDOR as visibleToVendor, ALRT.VISIBILITY_PENSKE as visibleToPenske, ALRT.ACTIONABLE, ALRT.HELP_TEXT, ALRT.MEASURE_VALUE, ALRT.COMPLIANCE_TEXT, TAB.TAB_ID, TAB.TAB_KEY FROM SMC.SMC_TAB_MASTER TAB INNER JOIN SMC.SMC_ALERT_HEADER HDR ON TAB.TAB_ID = HDR.TAB_ID INNER JOIN SMC.SMC_ALERTS ALRT ON HDR.HEADER_ID = ALRT.HEADER_ID LEFT JOIN SMC.SMC_SEARCH_TEMPLATES ST ON ALRT.TEMPLATE_ID = ST.TEMPLATE_ID WHERE TAB.TAB_KEY = #{tab.tabKey}
AND ALRT.HEADER_ID = #{headerId}
AND ALRT.VISIBILITY_PENSKE = 1
AND ALRT.VISIBILITY_VENDOR = 1
ORDER BY ALRT.DISPLAY_SEQUENCE

SELECT SUM(CASE WHEN CNT_OF_WORKING = 'Y' THEN 1 ELSE 0 END) AS workingCount, SUM(CASE WHEN CNT_OF_PENDED = 'Y' THEN 1 ELSE 0 END) AS pendingCount, SUM(CASE WHEN CNT_OF_READY_TO_ORDER = 'Y' THEN 1 ELSE 0 END) AS readyToOrderCount, SUM(CASE WHEN CNT_OF_CONTRACT_RVW = 'Y' THEN 1 ELSE 0 END) AS contractReviewCount, (SELECT COUNT (DISTINCT VENDOR_ID) FROM SMC.SMC_VENDOR_MASTER WHERE PLANNING_ANALYST IS NULL AND STATUS = 'A') AS vendorAnalystAssignmentRequiredCount, (SELECT COUNT (DISTINCT VENDOR_ID) FROM SMC.SMC_VENDOR_MASTER WHERE STATUS = 'A' AND VENDOR_ID NOT IN ( SELECT VENDOR_ID FROM SMC.SMC_ORG_VENDOR_ASSOC WHERE ORG_ID != 1 )) AS newVendorSetupRequiredCount, (SELECT COUNT (DISTINCT VND.VENDOR_ID) FROM SMC.SMC_VENDOR_MASTER VND INNER JOIN SMC.SMC_ORG_VENDOR_ASSOC VNDASSOC ON VND.VENDOR_ID = VNDASSOC.VENDOR_ID AND VNDASSOC.ORG_ID != 1 WHERE VNDASSOC . ORG_ID NOT IN ( SELECT DISTINCT ORG_ID FROM SMC . SMC_USER_MASTER WHERE TYPE_ID = 2 ) AND VND.STATUS = 'A' ) AS vendorUserSetupRequiredCount FROM SMC.SMC_ORDER_FULFILLMENT_SUMMARY OFSUM WHERE BUYER_SSO IN (
#{sso}
)

SELECT SUM(CASE WHEN CNT_OC_CANC_ORDER = 'Y' THEN 1 ELSE 0 END) AS cancellationCount, SUM(CASE WHEN CNT_OC_CHANGE_ORDER = 'Y' THEN 1 ELSE 0 END) AS changeOrderCount, SUM(CASE WHEN CNT_OC_PURCHASE_ORDER = 'Y' THEN 1 ELSE 0 END) AS purchaseOrderCount FROM SMC.SMC_ORDER_CONFIRMATION_SUMMARY OCSUM
1 = -1
VENDOR_ID IN (
#{associatedVendorId}
)
AND VENDOR_VISIBILITY = 'Y'
