mapper.select:
SELECT u.USER_ID, u.SSO, u.FIRST_NAME, u.LAST_NAME, u.EMAIL, u.PHONE, u.TYPE_ID, u.ORG_ID, u.ROLE_ID, u.DEPT_ID, org.NAME AS ORG_NAME,
'BOTH' 'SECURITY' 'VENDOR' 'NONE' AS LOAD_SECURITY_FUNCTIONS_AND_VENDORS FROM SMC.SMC_USER_MASTER u LEFT JOIN SMC.SMC_ORG_MASTER org ON org.ORG_ID = u.ORG_ID WHERE u.STATUS = 'A'
AND u.SSO = #{sso}
AND u.TYPE_ID = #{userType}
AND u.DEPT_ID = #{userDepartment}
ORDER BY u.FIRST_NAME, u.LAST_NAME

SELECT DISTINCT(SELECTION_TYPE) 
FROM SMC.SMC_BUDDIES 
WHERE SSO=#{sso} 
AND BUDDY_SSO=#{sso}

SELECT SSO AS buddySso FROM SMC.SMC_USER_MASTER
( TYPE_ID=1
AND DEPT_ID=1
AND DEPT_ID=2
) OR SSO=#{sso}

SELECT VEND.VENDOR_ID FROM SMC . SMC_VENDOR_MASTER VEND INNER JOIN SMC.SMC_USER_MASTER USR ON VEND.SUPPLY_SPECIALIST = USR.USER_ID OR VEND.PLANNING_ANALYST = USR.USER_ID WHERE USR.SSO IN (
#{sso}
)
